import Navbar from '../common/navbar/Navbar';
import styles from './profile.module.scss';

import { useEffect, useState } from 'react';

function Farm()  {


    const [farmer, setFarmer] = useState();

    const user = JSON.parse(localStorage.getItem('user'));
    console.log("user", user)

    if (!user || user.role !== 'farmer') {
        window.location = "/adminaccess"
    }

    useEffect(() => {
        axios.get(`${url}/api/farmer/email/${user.email}`, {
            "Content-Type": "application/json",
        })
            .then(function (response) {
                console.log(response);
                setFarmer(response.data.data[0]);
            })
            .catch(function (error) {
                console.log(error);
            });
    }, [])

    console.log(farmer)

    return (
        <>
            <Navbar />
            <div className={styles['container']}>
                <div>
                <div className={styles['form-title']}>
                    Farm Profile
                </div>

                <div className={styles['form-body']}>
                    <div className={styles['form-title']}>
                        Farm address
                    </div>
                    <div className={styles['input-field']}>
                        <input type="text" placeholder="Name" />
                    </div>
                    <div className={styles['input-field']}>
                        <input type="text" placeholder="Address" />
                    </div>
                    <div className={styles['input-field']}>
                        <input type="text" placeholder="City" />
                    </div>

                    <div className={styles['row']}>
                    <div className={styles['input-field']}>
                        <input type="text" placeholder="Country" />
                    </div>
                    <div className={styles['input-field']}>
                        <input type="text" placeholder="Zip Code" />
                    </div>
                </div>
                </div>

                <div className={styles['navigate']}>
                    <div className={styles['navigate-btn']}>
                        <button className={styles['blue']}>Save</button>
                    </div>
                    <div className={styles['navigate-btn']}>
                        <button className={styles['blue']}>Farmland</button>
                    </div>
                </div>
                </div>

            </div>
        </>
    )
}

export default Farm;
