import { useState } from 'react';
import styles from './auth.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
import { url } from '../utils/constants';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Login() {

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        role: ''
    });


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
    };

    const onLogin = () => {
        axios.post(`${url}/api/user/signup`, {
            email: formData.email,
            password: formData.password  
        })
        .then(function (response) {
            console.log(response);
            toast.success(response.message);
        })
        .catch(function (error) {
            console.log(error);
            toast.error(error.message);
        });
    }

    console.log(formData)

    return (
        <div className={styles['container']}>
            <ToastContainer />
            <div className={styles['banner']}>
                <img src='https://i.imgur.com/XPQO6IV_d.webp' />
            </div>
            <div className={styles['form']}>
                <div className={styles['header']}>
                    Log In
                </div>
                <div className={styles['input-field']}>
                    <div className={styles['input-item']}>
                        <input type="text" namer="email" placeholder="E-mail"  />
                    </div>
                    <div className={styles['input-item']}>
                        <input type="password" name="password" onChange={handleChange} placeholder="Password"  />
                    </div>

                    <input type="radio" id="html" name="fav_language" onChange={handleChange} value="HTML" />
                    <label for="html">Farmer</label><br />
                    <input type="radio" id="css" name="fav_language" onChange={handleChange} value="CSS" />
                    <label for="css">Pilot</label><br />
                    <input type="radio" id="javascript" name="fav_language" onChange={handleChange} value="JavaScript" />
                    <label for="javascript">Admin</label>

                    <div className={styles['input-button']}>
                        <button onClick={onLogin}>Log in</button>
                    </div>
                </div>
                <div className={styles['form-footer']}>
                    Don't have an account? Sign up
                </div>

                <div className={styles['google-auth']}>
                    <button><FontAwesomeIcon icon="fa-brands fa-google" />Log in with Google</button>
                </div>
            </div>

        </div>
    )
}

export default Login;
